package org.tnsif.SingleDimensionArray;

public class ArrayExample {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		//Declaration and initialization of array at compile time
		int arr[]= {1,2,3,4,5};
		System.out.println("2nd index element of array:"+arr[2]);
		//to print the array value 
		for(int i:arr)
		{
			System.out.print(i+" ");
		}
	}

}
